import React, { useRef } from "react";
import { Link } from "react-router-dom";

const Dashboard = () => {
  const videos = Array.from({ length: 20 }, (_, i) => `Video ${i + 1}`);
  const carouselRef = useRef(null);

  const scrollLeft = () => {
    carouselRef.current.scrollBy({ left: -300, behavior: "smooth" });
  };

  const scrollRight = () => {
    carouselRef.current.scrollBy({ left: 300, behavior: "smooth" });
  };

  return (
    <div className="d-flex" style={{ height: "100vh" }}>
      {/* Sidebar */}
      <nav
        className="bg-dark text-light p-3"
        style={{ width: "250px", minHeight: "100vh" }}
      >
        <h4 className="text-light mb-4">Dashboard</h4>
        <ul className="nav flex-column">
          <li className="nav-item mb-3">
            <Link to="/account" className="nav-link text-light">
              Account
            </Link>
          </li>
          <li className="nav-item mb-3">
            <Link to="/upload" className="nav-link text-light">
              Upload
            </Link>
          </li>
          <li className="nav-item mb-3">
            <Link to="/" className="nav-link text-light">
              Videos
            </Link>
          </li>
        </ul>
      </nav>

      {/* Main Content */}
      <div className="flex-grow-1 p-4 bg-secondary position-relative">
        <h2 className="text-light">Videos</h2>
        <div className="carousel-container position-relative">
          {/* Left Navigation Button */}
          <button
            className="btn btn-primary position-absolute"
            style={{
              left: "10px",
              top: "50%",
              transform: "translateY(-50%)",
              zIndex: 1,
            }}
            onClick={scrollLeft}
          >
            ◀
          </button>

          {/* Carousel */}
          <div
            ref={carouselRef}
            className="video-carousel d-flex"
            style={{
              overflowX: "auto",
              whiteSpace: "nowrap",
              gap: "20px",
              padding: "10px 0",
              scrollBehavior: "smooth",
            }}
          >
            {videos.map((video, index) => (
              <div
                key={index}
                className="video-thumbnail text-center"
                style={{
                  flex: "0 0 auto",
                  width: "250px",
                  backgroundColor: "#2c3e50",
                  borderRadius: "8px",
                  padding: "10px",
                  boxShadow: "0px 5px 15px rgba(0, 0, 0, 0.3)",
                }}
              >
                <img
                  src={`https://via.placeholder.com/250x150?text=${video}`}
                  alt={video}
                  className="rounded mb-2"
                  style={{ width: "100%", borderRadius: "8px" }}
                />
                <p className="text-light m-0">{video}</p>
              </div>
            ))}
          </div>

          {/* Right Navigation Button */}
          <button
            className="btn btn-primary position-absolute"
            style={{
              right: "10px",
              top: "50%",
              transform: "translateY(-50%)",
              zIndex: 1,
            }}
            onClick={scrollRight}
          >
            ▶
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
